import http from "./httpService";
import { apiUrl } from "../config.json";
const apiGetAccessCode  = apiUrl + "/login/createNewAccessCode";
const apiValidatePhoneNumber = apiUrl + "/login/validatePhoneNumber";
const tokenKey  = "token"; // key-value name

export async function getAccessCode(phoneNumber) {
    const {data:{token:jwt}} = await http.post(apiGetAccessCode, { phoneNumber });
    localStorage.setItem(tokenKey, jwt);
}

export async function VerificationCode(accessCode) {
    console.log('im here sent')
    const {data:{token:jwt}} = await http.post(apiSendAccessCode, {accessCode});
}

export async function validatePhoneNumber(phoneNumber) {
    const response = await http.post(apiValidatePhoneNumber, {phoneNumber});
}
export default {
    getAccessCode,
    sendAccessCode
};
