export const phoneValidator = phoneNumber => {
    if (!phoneNumber || phoneNumber.length <= 0) return "Phone number cannot be empty.";
    var phoneno = /^\d{10}$/;
    if(phoneNumber.value.match(phoneno)) {
        return "";
    } else {
        return "Invalid Phone Number";
    }
    return "";

}
