import React, { useState } from 'react';
// Material UI Stuffs
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import SmartphoneIcon from '@material-ui/icons/Smartphone';
import Typography from '@material-ui/core/Typography';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import auth from '../Services/apiService';
import { phoneValidator } from '../validation/PhoneValidation';

const useStyles = makeStyles((theme) => ({
    box: {
        marginTop: theme.spacing(7),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    icon: {
        margin: theme.spacing(1),
        backgroundColor: '#4EA69E',
    },
    form: {
        margin: theme.spacing(1),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
    error: {
        color: 'red',
        fontSize: 12,
    },
}))

export default function Login() {
    const classes = useStyles();

    // Input from user
    const [phoneNumber, setPhoneNumber] = useState({ value: "", error: "" });
    // Entered Access Code
    const [accessCode, setAccessCode] = useState({ value: "", error: "" });
    // Open pop-up window
    const [open, setOpen] = React.useState(false);

    const handleChange = event => {
        setPhoneNumber({ value: event.target.value, error: ""});
    }
    const handleSubmit = async (event) => {
            event.preventDefault();
            const phoneNumberError = phoneValidator(phoneNumber.value);

            if(phoneNumberError) {
                setPhoneNumber({...phoneNumber, error: phoneNumberError});
                return;
            }

            auth.getAccessCode(phoneNumber.value)
                .then(() => {
                    // window.location = '/enterAccessCode';
                    setOpen(true);
                })
                .catch(function(error) {
                    console.error("Error writing document: ", error);
                });
    }
    const handleClose = () => {
        setOpen(false);
    };
    return (
        <Container component="main" maxWidth='xs'>
            <CssBaseline />
            <div className={classes.box}>
                <Avatar className={classes.icon}>
                    <SmartphoneIcon/>
                </Avatar>
                <Typography component="h1" variant="h5">
                    Sign in
                </Typography>
                <form className={classes.form} noValidate>
                    <TextField
                        variant="outlined"
                        margin="normal"
                        required
                        fullWidth
                        label="Phone Number"
                        value={phoneNumber.value}
                        onChange={handleChange}
                        helperText="Please enter your phone number"
                    />

                    {phoneNumber.error ? <Typography component="p" className={classes.error}>{phoneNumber.error}</Typography> : null}
                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        color="primary"
                        className={classes.submit}
                        onClick={handleSubmit} 
                    >
                        Submit
                    </Button>
                </form>
                <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title">Please Enter 6-digit Access Code</DialogTitle>
                    <DialogContent>
                    <DialogContentText>
                    An access code has been sent to your phone
                    </DialogContentText>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="name"
                        label="Email Address"
                        type="email"
                        fullWidth
                    />
                    </DialogContent>
                    <DialogActions>
                    <Button onClick={handleClose} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={handleClose} color="primary">
                        Subscribe
                    </Button>
                    </DialogActions>
                </Dialog>
            </div>
        </Container>
    )
}
