import React, { useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import LockOpenIcon from '@material-ui/icons/LockOpen';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import auth from '../Services/apiService';
import { phoneValidator, accessCodeValidator } from '../validation/PhoneValidation';

const useStyles = makeStyles((theme) => ({
    box: {
        marginTop: theme.spacing(7),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    icon: {
        margin: theme.spacing(1),
        backgroundColor: '#4EA69E',
    },
    form: {
        margin: theme.spacing(1),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
    error: {
        color: 'red',
        fontSize: 12,
    },
}))

export default function EnterAccessCode() {
    const classes = useStyles();

    // Input from user
    const [accessCode, setAccessCode] = useState({ value: "", error: "" });

    const handleChange = event => {
        setAccessCode({ value: event.target.value, error: ""});
    }
    const handleSubmit = (event) => {
            event.preventDefault();
            const accessCodeError = "";

            // if(accessCodeError) {
            //     setAccessCode({...accessCode, error: accessCodeError});
            //     return;
            // }

            // auth.getAccessCode(phoneNumber.value)
            // .then(() => {
            //     console.log("Access Code is dending to your phone!!!");
            // })
            // .catch(function(error) {
            //     console.error("Error writing document: ", error);
            // });
    }

    return (
        <Container component="main" maxWidth='xs'>
            <CssBaseline />
            <div className={classes.box}>
                <Avatar className={classes.icon}>
                    <LockOpenIcon/>
                </Avatar>
                <Typography component="h1" variant="h5">
                    Please enter 6-digit access code
                </Typography>
                <form className={classes.form} noValidate>
                    <TextField
                        variant="outlined"
                        margin="normal"
                        required
                        fullWidth
                        label="Access Code"
                        value={setAccessCode.value}
                        onChange={handleChange}
                        helperText="An access code has been sent to your phone"
                    />

                    {accessCode.error ? <Typography component="p" className={classes.error}>{accessCode.error}</Typography> : null}
                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        color="primary"
                        className={classes.submit}
                        onClick={handleSubmit} 
                    >
                        Submit
                    </Button>
                </form>
            </div>
        </Container>
    )
}
