import React, { useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import SmartphoneIcon from '@material-ui/icons/Smartphone';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import http from "../Services/httpService";
import { apiUrl } from "../config.json";
import auth from '../Services/apiService';
import { phoneValidator } from '../validation/PhoneValidation';



const useStyles = makeStyles((theme) => ({
    box: {
        marginTop: theme.spacing(7),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    icon: {
        margin: theme.spacing(1),
        backgroundColor: '#4EA69E',
    },
    form: {
        margin: theme.spacing(1),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
    error: {
        color: 'red',
        fontSize: 12,
    },
}))

export default function Login() {
    const classes = useStyles();

    // Input from user
    const [phoneNumber, setPhoneNumber] = useState({ value: "", error: "" });

    const handleChange = event => {
        setPhoneNumber({ value: event.target.value, error: ""});
    }
    const handleSubmit = async (event) => {
            event.preventDefault();
            const phoneNumberError = phoneValidator(phoneNumber.value);

            if(phoneNumberError) {
                setPhoneNumber({...phoneNumber, error: phoneNumberError});
                return;
            }

            auth.getAccessCode(phoneNumber.value)
                .then(() => {
                    window.location = '/enterAccessCode';
                })
                .catch(function(error) {
                    console.error("Error writing document: ", error);
                });
    }

    return (
        <Container component="main" maxWidth='xs'>
            <CssBaseline />
            <div className={classes.box}>
                <Avatar className={classes.icon}>
                    <SmartphoneIcon/>
                </Avatar>
                <Typography component="h1" variant="h5">
                    Sign in
                </Typography>
                <form className={classes.form} noValidate>
                    <TextField
                        variant="outlined"
                        margin="normal"
                        required
                        fullWidth
                        label="Phone Number"
                        value={phoneNumber.value}
                        onChange={handleChange}
                        helperText="Please enter your phone number"
                    />

                    {phoneNumber.error ? <Typography component="p" className={classes.error}>{phoneNumber.error}</Typography> : null}
                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        color="primary"
                        className={classes.submit}
                        onClick={handleSubmit} 
                    >
                        Submit
                    </Button>
                </form>
            </div>
        </Container>
    )
}
